function wcSetClipboard(g,e){if("undefined"===typeof e){e=jQuery(document)}var f=jQuery('<textarea style="opacity:0">');jQuery("body").append(f);f.val(g).select();e.trigger("beforecopy");try{document.execCommand("copy");e.trigger("aftercopy")}catch(h){e.trigger("aftercopyfailure")}f.remove()}function wcClearClipboard(){wcSetClipboard("")};